import React, { Component } from "react";
import "./Homepage.css";
import { Link } from "react-router-dom";
// import * as data from "../data/countries.geojson";


export default class Homepage extends Component {

    state = {
        latestNews: [], 
        currentCountryCaseData: null,
        newsForCurrentCountry: []
    }


    componentDidMount(){
        fetch(`https://gnews.io/api/v3/search?q=coronavirus%20news&token=6385a60f5d387a9bf5fd4f8d033f4168`)
            .then(response => response.json()).then(news => 
                this.setState({latestNews: news.articles&& news.articles
            }))
            .catch(err => {
                console.log(err);
            });


        fetch("http://ip-api.com/json").then(resp => resp.json()).then(resp => 
            fetch("https://coronavirus-monitor.p.rapidapi.com/coronavirus/cases_by_country.php", {
              "method": "GET",
              "headers": {
                  "x-rapidapi-host": "coronavirus-monitor.p.rapidapi.com",
                  "x-rapidapi-key": "36d86838d4mshba08a92dbba7104p1ae352jsnf92cd7cb9983"
              }
          })
          .then(response => response.json()).then(data => 
              this.setState({caseData: data.countries_stat
              }))
          .catch(err => {
          console.log(err);
          })
        )
    }

  render() {
    const sortedNews  = this.state.latestNews&& this.state.latestNews[0]? this.state.latestNews.sort((a,b) => {
        return new Date(b.publishedAt) - new Date(a.publishedAt);
    }):
    undefined

    const top5News = sortedNews? sortedNews.slice(0, 5) : undefined
    const { currentCountry } = this.state
    return (
      <div className="div">
        <h1 className="h1">CorTrack</h1>
        <Link to="/map">
          <button className="nav-btn">Map</button>
        </Link>
        <Link to="/case-info">
          <button className="nav-btn">Cases Info</button>
        </Link>

        <div className='result'> 
            {currentCountry && currentCountryCaseData&&
            <>
            <div className='case-box'>
                <h1 className='txt'>Country: {country.country_name}</h1>
                <h2 className='txt'>Cases: {country.cases}</h2>
                <h2 className='txt'>Deaths: {country.deaths}</h2>
                <h2 className='new-case-txt'>New Cases: {country.new_cases}</h2>
                <h2 className='new-death-txt'>New Deaths: {country.new_deaths}</h2>
                <h2 className='recover-txt'>Total Recovered: {country.total_recovered}</h2>
                <h3 className='txt'>Percent of infected who have died: {(parseFloat(country.deaths.replace(/,/g, ''))/parseFloat(country.cases.replace(/,/g, '')) * 100).toFixed(2) + "%"}</h3>
                <h3 className='txt'>Percent of infected who have recovered: {(parseFloat(country.total_recovered.replace(/,/g, ''))/parseFloat(country.cases.replace(/,/g, '')) * 100).toFixed(2) + "%"}</h3>
            </div>
            <div className='case-box'>
              <h1 className='txt'>{country.country_name}'s Latest News:</h1>
                {!top3News? <h2 className='txt'>None</h2> : undefined}
                {top3News&& 
                top3News.map(article => (
                    <h3 className='news-txt'><a className='news-txt' href={article.url}>{article.title}</a></h3>
                ))
                }
            </div> <br/>
            </>
            }
        </div>

        {this.state.latestNews&& this.state.latestNews[0]&&
        <h1 className="h1 news-title"><u>Latest Global News</u></h1>
        }
        <div className='latest-news'>
            {top5News&&
            top5News.map(newsPiece => (
                <h3 className='news-txt'><a className='news-txt' href={newsPiece.url}>{newsPiece.title}</a></h3>
            ))}
        </div>

      </div>
    );
  }
}
